  const onFileChange = (e) => {
    const file = e.target.files[0];
    props.onSelectImage(file);
  };

  const customInputLat = () => {
    return (
      <div>
        <input
          className="textInput grosir"
          name="latitude"
          type="text"
          defaultValue={lat}
          disabled
        />
      </div>
    );
  };

  const customInputLong = () => {
    return (
      <div>
        <input
          className="textInput grosir"
          name="longitude"
          type="text"
          defaultValue={long}
          disabled
        />
      </div>
    );
  };

  const renderMyMaps = () => {
    const { places, mapApiLoaded, mapInstance, mapApi } = this.state;

    const apiKey = 'AIzaSyC81o8Qoasn_E2FqMrqhmM-2IUlgAab2V4'

    const defaultZoom = 11;
    const defaultCenter = { lat: parseFloat(this.state.lat), lng: parseFloat(this.state.lng) }
    const locations = [
      {
        lat: parseFloat(this.state.lat),
        lng: parseFloat(this.state.lng),
        draggable: false,
        title: this.state.address,
      },
    ]

    const MarkerList = () => {
      return locations.map((location, index) => {
        return (
          <MarkerWithInfoWindow key={index.toString()} location={location}/>
        )
      })
    }

    const MarkerWithInfoWindow = ({location}) => {
      // const [isOpen, setIsOpen] = React.useState(false)

      return (
        <Marker 
          onClick={() => this.setState({isOpen: !this.state.isOpen})} 
          position={location} 
          title={location.title} 
          label={location.label}
        >
          { this.state.isOpen &&
            <InfoWindow onCloseClick={() => this.setState({isOpen: false})}>
              <CNavLink href={location.www} target="_blank">{location.title}</CNavLink>
            </InfoWindow>
          }
        </Marker>
      )
    }

    const GoogleMapsComponent = withScriptjs(withGoogleMap(() => {
        return (
          <GoogleMap defaultZoom={defaultZoom} defaultCenter={defaultCenter}>
            {<MarkerList locations={locations}/>}
          </GoogleMap>
        )
      }
    ))

    return (
      <Wrapper>
        <GoogleMapsComponent
          key="map"
          googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=${apiKey}`}
          loadingElement={<div style={{height: `100%`}}/>}
          containerElement={<div style={{height: `400px`}}/>}
          mapElement={<div style={{height: `100%`}}/>}
        />
      </Wrapper>
    );
  }

  return (
    <form onSubmit={handleSubmit}>
      <main className="c-main">
        <div className="container-fluid">
          <CCard>
            <CCardHeader>
              <CRow>
                <CCol xs={9} md={10} lg={11} style={{ margin: "auto" }}>
                  <h4 style={{ margin: "auto" }}>Add Coffee Origin</h4>
                </CCol>
                <CCol>
                  <CButton
                    block
                    color="dark"
                    to="/listSupplier"
                    style={{ backgroundColor: "#00c4cc" }}
                  >
                    <span style={{ color: "white" }}>X</span>
                  </CButton>
                </CCol>
              </CRow>
            </CCardHeader>
            <CCardBody>
              <CForm action="" method="post">
                <CFormGroup>
                  <CLabel htmlFor="nf-namaSupplier">Processor Name</CLabel>
                  <Field
                    className="textInput grosir"
                    name="nama_supplier"
                    component="input"
                    type="text"
                    required
                  />
                </CFormGroup>
                <CFormGroup>
                  <CLabel htmlFor="nf-lokasiSupplier">Processor Origin</CLabel>
                  <Field
                    className="textInput grosir"
                    name="lokasi_supplier"
                    component="input"
                    type="text"
                    required
                  />
                </CFormGroup>
                <CFormGroup>
                  <CLabel htmlFor="nf-kebun">Farm</CLabel>
                  <div className="main-wrapper">
                    <MyGoogleMap onMarkerHandle={markerHandle} />
                    {/* <MyGoogleMap onMarkerHandle={markerHandle} latitude={lat} longitude={long} /> */}
                  </div>
                </CFormGroup>
                <CFormGroup row>
                  <CLabel col xs="6" htmlFor="nf-latitude">
                    Latitude
                  </CLabel>
                  <CLabel col xs="6" htmlFor="nf-longitude">
                    Longitute
                  </CLabel>
                  <CCol xs="6">
                    <Field
                      className="textInput grosir"
                      name="longitude"
                      component={customInputLat}
                      type="text"
                      required
                    />
                  </CCol>
                  <CCol xs="6">
                    <Field
                      className="textInput grosir"
                      name="longitude"
                      component={customInputLong}
                      type="text"
                      required
                    />
                  </CCol>
                </CFormGroup>
                <CFormGroup>
                  <CLabel htmlFor="nf-lokasiSupplier">Link Maps (optional)</CLabel>
                  <Field
                    className="textInput grosir"
                    name="linkMaps"
                    component="input"
                    type="text"
                    // onChange={GetLinkLokasi}
                  />
                </CFormGroup>
                <CFormGroup row>
                  <CLabel col xs="6" htmlFor="nf-elevation">
                    Elevation
                  </CLabel>
                  <CLabel col xs="6" htmlFor="nf-unit">
                    Unit
                  </CLabel>
                  <CCol xs="6">
                    <Field
                      className="textInput grosir"
                      name="elevation"
                      component="input"
                      type="text"
                      required
                    />
                  </CCol>
                  <CCol xs="6">
                    <Field
                      className="textInput grosir"
                      name="unit"
                      component="select"
                      required
                    >
                      <option value="" selected disabled hidden>
                        ----
                      </option>
                      {unit &&
                        unit.map((value) => {
                          return (
                            <option key={value.id} value={value.id}>
                              {value.nama_unit}
                            </option>
