                    </Field>
                  </CCol>
                </CFormGroup>
                <CFormGroup>
                  <CLabel htmlFor="nf-namaPetani">Farmer Name</CLabel>
                  <Field
                    className="textInput grosir"
                    name="nama_petani"
                    component="input"
                    type="text"
                    required
                  />
                </CFormGroup>
                <CFormGroup row>
                  <CLabel col xs="12" htmlFor="nf-estimatedProduction">
                    Estimated Production
                  </CLabel>
                  <CCol xs="10">
                    <Field
                      className="textInput grosir"
                      name="estimatedProduction"
                      component="input"
                      type="number"
                      required
                    />
                  </CCol>
                  <CCol xs="2" style={{ margin: "auto" }}>
                    <span>Ton</span>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CLabel col xs="12" htmlFor="file-gambarPetani">
                    Farmer Image
                  </CLabel>
                  <CCol xs="12">
                    <CInputFile
