import { Fragment, useState, useEffect } from "react";
import AddCuppingForm from "./AddCuppingForm";
import showResults from "../../../../showResults/showResults";
import UserService from "../../../../../services/user.service";

const AddCupping = () => {

  const [certificateFile, setCertificateFile] = useState("");

  const handleSubmit = (values) => {
    const formData = new FormData();

     if (
      values.curated_date &&
      values.curated_by &&
      values.fragrance_aroma &&
      values.flavor &&
      values.aftertaste &&
      values.acidity &&
      values.body &&
      values.cupping_score &&
      values.category &&
      certificateFile != ""
    ) {
      formData.append("name", values.name);
      formData.append("product_group", values.productGroup);
      formData.append("category", values.category);
      formData.append("subcategory", values.subcategory);
      formData.append("description", values.description);
      formData.append("short_description", values.short_description);
      formData.append("price", values.price);

      var raw = JSON.stringify(values);
      UserService.addCupping(raw).then(
        showResults("Data has been added")
      );
    } else {
      showResults("Please fill all the fields");
    }
  };

  const onCertificateChange = (file) => {
    setCertificateFile(file);
  };

  return (
    <Fragment>
      <AddCuppingForm 
        onSubmit={handleSubmit}
        onSelectCertificate={onCertificateChange} />
    </Fragment>
  );
};

export default AddCupping;
