import { Fragment } from "react";
import showResults from "../../../../showResults/showResults";
import UserService from "../../../../../services/user.service";
import { useParams } from "react-router";
import EditCuppingForm from "./EditCuppingForm";

const EditCupping = () => {
  const { id } = useParams();

  const handleSubmit = (values) => {
    var raw = JSON.stringify(values);
    UserService.editRoastingProfile(id, raw).then(showResults("Data has been changed"));
  };

  return (
    <Fragment>
      <EditCuppingForm onSubmit={handleSubmit} roastingProfileID={id} />
    </Fragment>
  );
};

export default EditCupping;
