                              <p>
                                <strong>Harvesting Year {i}:</strong>{" "}
                                {value.batch.tgl_panen}
                              </p>
                              <p>
                                <strong>Variety Name {i}:</strong>{" "}
                                {value.batch.jenis.nama_jenis}
                              </p>
                              <p>
                                <strong>Process Name {i}:</strong>{" "}
                                {value.batch.proses.nama_proses}
                              </p>
