                            <CCarouselControl direction="prev" />
                            <CCarouselControl direction="next" />
                          </CCarousel>
                        </CCardBody>
                      </CCard>
                    </CCol>
                    <hr
                      style={{
                        marginTop: "60px",
                        color: "#178d88",
                        backgroundColor: "#178d88",
                        height: 2,
                      }}
