                    {renderMyMaps()}
                </div>
              </CFormGroup>
              <CFormGroup row>
                <CLabel col xs="6" htmlFor="nf-latitude">
                  Latitude
                </CLabel>
                <CLabel col xs="6" htmlFor="nf-longitude">
                  Longitute
                </CLabel>
                <CCol xs="6">
                  <input
                    className="textInput grosir"
                    name="latitude"
                    component="input"
                    type="text"
                    value={lat}
                    onChange={(lat) => setLat(lat.currentTarget.value)}
                    disabled
                  />
                </CCol>
                <CCol xs="6">
                  <input
                    className="textInput grosir"
                    name="longitude"
                    component="input"
                    type="text"
                    value={long}
                    onChange={(lng) => setLong(lng.currentTarget.value)}
                    disabled
                  />
                </CCol>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="nf-lokasiSupplier">Link Maps (optional)</CLabel>
                <input
                  className="textInput grosir"
                  name="linkMaps"
                  component="input"
                  type="text"
                  defaultValue={linkMaps}
                  disabled
                  // onChange={GetLinkLokasi}
                />
              </CFormGroup>
              <CFormGroup row>
                <CLabel col xs="6" htmlFor="nf-elevation">
                  Elevation
                </CLabel>
                <CLabel col xs="6" htmlFor="nf-unit">
                  Unit
                </CLabel>
                <CCol xs="6">
                  <input
                    className="textInput grosir"
                    name="elevation"
                    component="input"
                    type="text"
                    defaultValue={elevation}
                    disabled
                  />
                </CCol>
                <CCol xs="6">
                  <input
                    className="textInput grosir"
                    name="unit"
                    component="input"
                    defaultValue={supplierUnit}
                    disabled
                  />
                </CCol>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="nf-namaPetani">Farmer Name</CLabel>
                <input
                  className="textInput grosir"
                  name="nama_petani"
                  component="input"
                  type="text"
                  defaultValue={namaPetani}
                  disabled
                />
              </CFormGroup>
              <CFormGroup row>
                <CLabel col xs="12" htmlFor="nf-estimatedProduction">
                  Estimated Production
                </CLabel>
                <CCol xs="10">
                  <input
                    className="textInput grosir"
                    name="estimatedProduction"
                    component="input"
                    type="number"
                    defaultValue={estimatedProduction}
                    disabled
                  />
                </CCol>
                <CCol xs="2" style={{ margin: "auto" }}>
                  <span>Ton</span>
                </CCol>
              </CFormGroup>
              <CFormGroup row>
                <CLabel col xs="12" htmlFor="file-gambarPetani">
                  Farmer Image
                </CLabel>
                <CCol xs="12" style={{textAlign:'center'}}>
                  <CImg
                    src={process.env.REACT_APP_CATALOG_URL + farmerImg}
                    // className="d-block w-100"
                    style={{width:"300px", height:"300px"}}
                  />
                </CCol>
              </CFormGroup>
            </CCardBody>
          </CCard>
        </div>
      </main>
    </form>
  );
};

